import { useChat } from 'context';
import { useResolved } from 'hooks';
import { ChatList } from 'components/ChatList';
import { RailHeader } from 'components/RailHeader';
import { Loader } from 'semantic-ui-react';

export const LeftRail = () => {
  const { myChats, createChatClick } = useChat();
  const chatsResolved = useResolved(myChats);

  return (
    <div className="left-rail">
      <RailHeader />
      {chatsResolved ? (
        <>
          {!!myChats.length ? (
            <div className="chat-list-container">
              <ChatList />
            </div>
          ) : (
            <div className="chat-list-container no-chats-yet">
              <h3>No Chats Yet</h3>
            </div>
          )}
          <button style={{ font: "Arial", backgroundColor: "gray", fontWeight:"bold"}} className="create-chat-button btn btn-secondary" onClick={createChatClick}>
            Create Chat
          </button>
        </>
      ) : (
        <div className="chats-loading">
          <Loader active size="huge" />
        </div>
      )}
    </div>
  );
};
